import React,{useState} from 'react'
// import PolicyControl from './PolicyControl';
// import ActivityControl from './ActivityControl';
import {
  Link, BrowserRouter as Router,
  Routes,
  Route
} from 'react-router-dom';


export default function SystemControl() {
  return (
    <div className='p-2' style={{background: '#DCDCDC', borderColor: '#707070',height:'82vh'}}>
        <div className='container-fluid' style={{background: '#A19B9E', border: '1px solid #707070', borderRadius:'5px', height:'80vh'}}>
          <div className="row justify-content-center p-0 m-0">
            <Link className='controlNav1 border border-dark text-center col-2'>
              <div >
                  Activity Control
              </div>
            </Link>
            <Link className='controlNav2 border border-dark text-center col-2' to="/policycontrol">
              <div>
                  Policy Control
              </div>
            </Link>
          <div>
            {/* <Routes>
              <Route path="/policycontrol" element={<PolicyControl/>} />
              <Route path="/activitycontrol" element={<ActivityControl/>} />                
            </Routes> */}
          </div>
          </div>
          
        </div>
    </div>
  )
}
