import {
  Link,
} from 'react-router-dom';

export default function SideBar() {
    let hoverstyle = {
        background: '#707070'
    }
    let normalstyle = {
        background: '#A19B9E'
    }
    const [styles, setStyles] = useState([
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle,
        normalstyle
    ])

    const handleHover = (index) => {
        let color = [
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle,
            normalstyle]
        color[index] = hoverstyle
        setStyles(color)
    }

    return (
    <div className='container-fluid my-2' style={{background: '#A19B9E', border: '1px solid #707070', borderRadius:'5px', minHeight:'80vh'}}>
        <div>
            
        </div>
                 
    </div>
  )
}
